mapper.update:
update lab_user
set updateTime = now()
,totalAddMoney = ifnull(totalAddMoney, 0) + ${totalAddMoney}
,totalUseMoney = ifnull(totalUseMoney, 0) - ${totalUseMoney}
,remainMoney = remainMoney + ${money}
where id = ${userId}

update lab_user
set updateTime = now()
,score = ${score}
where id = ${userId}
